import React, { useState } from "react";
import { CountButton } from "../CountButton";
import './Product.scss';
import im from "../../images/Basil.jpeg"

export const Product: React.FC = ({  }) => {
  const [amount, setAmount] = useState<string>('Оберіть кількість');
  const [count, setCount] = useState<number>(0);

  return (
    <div className="product">
      <div className="product__picture" style={{backgroundImage: `url(${im})`}}></div>

      <div className="product__info">
        Опис
      </div>

      <button
        type="button"
        className="product__amount"
      >
        Оберіть вагу
      </button>

      <div className="product__counter">
        <CountButton
          count={count}
          setCount={setCount}
          value={"-"}
        />

        <div className="product__counter__count">
          {count}
        </div>

        <CountButton
          count={count}
          setCount={setCount}
          value={"+"}
        />
      </div>

      <button
        type="button"
        className="product__amount"
      >
        До кошика
      </button>
    </div>
  );
};
