import React, { useState } from "react";
import { CountButton } from "../CountButton";
import './Product.scss';

type Props = {
  image: string,
  product: string,
}

export const Product: React.FC<Props> = ({ image, product }) => {
  const [amount, setAmount] = useState<string>('Оберіть вагу');
  const [count, setCount] = useState<number>(0);

  return (
    <div className="product">
      <div className="product__picture" style={{backgroundImage: `url(${image})`}}></div>

      <div className="product__info">
        {product}
      </div>

      <button
        type="button"
        className="product__amount"
      >
        {amount}
      </button>

      {amount !== "Оберіть вагу" && (
        <>

        </>
      )}
      <div className="product__counter">
        <CountButton
          count={count}
          setCount={setCount}
          value={"-"}
        />

        <div className="product__counter__count">
          {count}
        </div>

        <CountButton
          count={count}
          setCount={setCount}
          value={"+"}
        />
      </div>

      <button
        type="button"
        className="product__amount"
      >
        До кошика
      </button>
    </div>
  );
};
